apf.getRules = function(node) {
    var rules = {};

    for (var w = node.firstChild; w; w = w.nextSibling) {
        if (w.nodeType != 1)
            continue;
        else {
            if (!rules[w[apf.TAGNAME]])
                rules[w[apf.TAGNAME]] = [];
            rules[w[apf.TAGNAME]].push(w);
        }
    }

    return rules;
};


